  public static int calculateFloor(String input){
    System.out.println(input);
    int counter = 0;
    for(int i = 0; i < input.length(); i++){
      if(input.charAt(i) == '('){
        counter++;
      }else if(input.charAt(i) == ')'){
        counter--;
      }
      if(counter == -1){
        return i + 1;
      }
    }
    return -1;
  }
